import React from "react";

// Online image URLs for each policy type
const policyImages = {
  Life: "data:image/jpeg;base64,",
  Health: "data:image/jpeg;base64,",
  Motor: "data:image/png;base64,",
  Home: "data:image/jpeg;base64,",
  Travel: "data:image/jpeg;base64,",
};

// Default fallback image
const defaultImage =
  "https://cdn-icons-png.flaticon.com/512/1828/1828884.png";

// Descriptions
const policyDescriptions = {
  Life: "Life insurance provides financial protection to your family in case of your unfortunate death. It helps secure their future needs.",
  Health: "Health insurance covers medical expenses, hospitalization costs, and treatment charges, reducing financial stress during illness.",
  Motor: "Motor insurance protects your vehicle against accidents, theft, and damages. It also covers third-party liabilities.",
  Home: "Home insurance provides coverage against fire, theft, and natural calamities, helping you protect your home and belongings.",
  Travel: "Travel insurance covers medical emergencies, trip cancellations, lost baggage, and travel-related risks during your journey.",
};

export default function PolicyInfoModal({ policy, onClose }) {
  if (!policy) return null;

  const imageUrl = policyImages[policy.policyType] || defaultImage;
  const description =
    policyDescriptions[policy.policyType] || "No description available.";

  return (
    <div className="fixed inset-0 bg-black/50 flex items-center justify-center z-50">
      <div className="bg-white rounded-xl shadow-lg p-6 w-96 relative">
        
        {/* Close Button */}
        <button
          className="absolute top-3 right-3 text-gray-500 text-xl"
          onClick={onClose}
        >
          ✕
        </button>

        {/* Image */}
        <img
          src={imageUrl}
          alt={policy.policyType}
          className="w-60 h-50 mx-auto mb-4"
        />

        <h2 className="text-xl font-bold text-center">
          {policy.policyType} Insurance
        </h2>

        <p className="mt-2 text-gray-600 text-sm text-center">{description}</p>

        <div className="mt-4 text-sm">
          <p><strong>Insurer:</strong> {policy.insurer}</p>
          <p><strong>Status:</strong> {policy.status}</p>
          <p><strong>Premium Amount:</strong> ₹{policy.premiumAmt.toFixed(2)}</p>
          <p><strong>Start Date:</strong> {new Date(policy.startDate).toLocaleDateString()}</p>
          <p><strong>End Date:</strong> {new Date(policy.endDate).toLocaleDateString()}</p>
        </div>
      </div>
    </div>
  );
}